import React, { useState } from 'react';
import Board from './Board';
import Keypad from './Keypad';
const Game  = () =>{
    const res = 'wendy';
    const [currRow, setCurrRow] = useState(0);

    const [currWord, setCurrWord] = useState('');
    const [squares, setSquares] = useState([...Array(30).fill('')]);
    const [letters, setLetters] = useState([...Array(26).fill('black')])
    const handleKeypadClick = (value) =>{
        let newWord = currWord + value;
        setCurrWord(newWord);
        let currIndex = currWord.length + Number(currRow * 5);
        let newSquares = [...squares];
        newSquares[currIndex] = value;
        setSquares(newSquares); 
        setCurrWord(newWord);
        if (currWord.length == 4){
            updateKeypad(newWord);
            console.log(newWord)
            setCurrRow(currRow+1);
            setCurrWord('');

        }
        console.log(letters);

    }
    const updateKeypad = (currWord) => {
        for (var i = 0; i < 5; i++){
            const target = currWord.charAt(i);
            const res2 = res.toLocaleUpperCase();

            if (target === res2.charAt(i)){

                const a = String(target);
                const index = Number(a.charCodeAt(0)-'A'.charCodeAt(0));
                setLetters(letters[index] = '#538D4E');

            }
            else if (res.indexOf(target) !== -1){
                const a = String(target);
                const index = Number(a.charCodeAt(0)-'A'.charCodeAt(0));
                setLetters( prev => prev[index] ='#B59F3B');


            }
            else {
                const a = String(target);
                const index = Number(a.charCodeAt(0)-'A'.charCodeAt(0));
                let newLetters = [...letters];

                setLetters(prev => prev[index] = '#3A3A3C');

            }


        }

    }
    return (
        <div className='game'>
            <Board squares = {squares} letters = {letters}></Board>
            <Keypad   handleClick = {handleKeypadClick}></Keypad>
        </div>

    )
}
export default Game;
