import React from 'react';
const Board = (props) =>{
    const renderSquare = (i, value) => (
        <div className='square'>{value}</div>
    )
    let count = 0;
    return (
        <div className='board'>
            {[...Array(6)].map((elem, i) =>(
                <div className='board-row'>
                    {[...Array(5)].map((elem, j) => {
                        count++;
                        return renderSquare(count-1, props.value);
                })}
                </div>
            ))}
        </div>
    )


}
export default Board;
