import { Row1, Row2 , Row3 } from '../data/Letters';
const Keypad = () =>{
    const renderKey = (value) =>{
        return(
            value === 'ENTER' || value === 'DEL'? <div className='big-key'>{value}</div> : <div className='small-key'>{value}</div>
        )
    }
    const arr1 = ['2', '3', '4']
    return (
        <div className='keypad'>
            <div className='key-row'>
                {arr1.map ((elem) =>(
                    renderKey({elem})
                ))}
            </div>
