import React from 'react';
const Board = (props) =>{

    const [prevColoredSquares, setPrevColoredSquares] = useState(...Array(30).fill(''));
    const renderSquare = (value, renderingRow, mainIndex) => {
        const ref = 'A';
        let color = '';
        if (prevColoredSquares[mainIndex] === ''){
            color = props.letters[value.charCodeAt(0) - ref.charCodeAt(0)];
            let newPrevColoredSquares = [...prevColoredSquares];
            newPrevColoredSquares[mainIndex] = color;
            setPrevColoredSquares(newPrevColoredSquares);

        }
        else {
            color = props.prevColoredSquares[mainIndex];

        }

        console.log(color);


        return (
            props.currRow-1 >= renderingRow? <div className='square' style = {{background :`${color}`}}>{value}</div>:
            <div className='square' >{value}</div>
        )
    }
    let count = 0;
    return (
        <div className='board'>
            {[...Array(6)].map((elem, i) =>(
                <div className='board-row'>
                    {[...Array(5)].map((elem, j) => {
                        count++;
                        return renderSquare(props.squares[count-1], i, count-1);
                })}
                </div>
            ))}
        </div>
    )


}
export default Board;
