import React, { useState } from 'react';
import Board from './Board';
import Keypad from './Keypad';
const Game  = () =>{
    const res = 'wendy';
    const [currRow, setCurrRow] = useState(0);
    const [currCol, setCurrCol] = useState(0);
    const [currWord, setCurrWord] = useState([...Array(5).fill('')]);
    const [squares, setSquares] = useState([...Array(30).fill('')]);
    const [letters, setLetters] = useState([...Array(26).fill('square-normal')])
    const handleKeypadClick = (value) =>{
        const currIndex = Number(currRow*5) + Number(currCol);
        //console.log(currIndex);
        const newSquares = [...squares];
        newSquares[currIndex] = value;
        setSquares([...newSquares]);
        const newWord = [...currWord];
        newWord[currCol] = value;
        setCurrWord([...newWord]);
        //console.log(newSquares);
        setCurrCol(Number(currCol%5)+1);
        if (currCol != 0 && currCol%5 === 0){
            setCurrRow(currRow+1);
            updateKeypad(currWord);
            setCurrWord([...Array(5).fill('')]);
        }
    }
    const updateKeypad = () =>{
        for (var i = 0; i < 5; i++){
            if (res.indexOf(currWord[i]) === i){
                const newLetters = [...letters];
                const ref = 'A';
                newLetters[currWord[i].charCodeAt(0)-ref.charCodeAt(0)] = 'square-green';
                setLetters(newLetters);
            }
            else if (res.indexOf(currWord[i]) != -1){
                const newLetters = [...letters];
                const ref = 'A';
                newLetters[currWord[i].charCodeAt(0)-ref.charCodeAt(0)] = 'square-green';
                setLetters(newLetters);
            }
        }

    }
    return (
        <div className='game'>
            <Board squares = {squares}></Board>
            <Keypad   handleClick = {handleKeypadClick}></Keypad>
        </div>

    )
}
export default Game;
