import React from 'react';
const Board = (props) =>{

    const renderSquare = (value, renderingRow) => {
        const ref = 'A';

        const color = props.letters[value.charCodeAt(0) - ref.charCodeAt(0)];
        console.log(color);
        return (
            props.currRow-1 >= renderingRow? <div className='square' style = {{background :`${color}`}}>{value}</div>:
            <div className='square' >{value}</div>
        )
    }
    let count = 0;
    return (
        <div className='board'>
            {[...Array(6)].map((elem, i) =>(
                <div className='board-row'>
                    {[...Array(5)].map((elem, j) => {
                        count++;
                        return renderSquare(props.squares[count-1], i);
                })}
                </div>
            ))}
        </div>
    )


}
export default Board;
