import React, { useState } from 'react';
import Board from './Board';
import Keypad from './Keypad';
const Game  = () =>{
    const [Row1Used, setRow1Used]= useState([...Array(10).fill(false)]);
    const [Row2Used, setRow2Used] = useState([...Array(9).fill(false)]);
    const [Row3Used, setRow3Used] = useState([...Array(9).fill(false)]);
    const handleKeypadClick = (i, row) =>{
        if (row === 'row1'){
            const Row1New = [...Row1Used];
            Row1New[i] = true;
            setRow1Used([...Row1New]);
        }
        if (row === 'row2'){
            const Row2New = [...Row2Used];
            Row2New[i] = true;
            setRow2Used([...Row2New]);
        }
        if (row === 'row3'){
            const Row3New = [...Row3Used];
            Row3New[i] = true;
            setRow3Used([...Row3New]);
        }
    }
    return (
        <div className='game'>
            <Board></Board>
            <Keypad handleClick = {handleKeypadClick}></Keypad>
        </div>

    )
}
export default Game;
