import React from 'react';

const Keypad = (props) =>{
    const handleKeypadClick = (i, row) =>{
        console.log('clicked')
        props.handleClick(i, row );
    }
    const renderKey = (value, row, i) =>{
        return(
            (value === 'ENTER' || value === 'DEL')? <div className='big-key'>{value}</div> : 
            ((row === 'row1' && props.Row1Used[i] === true) ||
            (row === 'row2' && props.Row2Used[i] === true) ||
            (row === 'row3' && props.Row3Used[i] === true))?
            <div  onClick = {() => handleKeypadClick(i, row)} className='small-key-used'>{value}</div> : <div onClick={() => handleKeypadClick(i, row)} className ='small-key'>{value}</div>
        )
    }
    const Row1  = [
        'Q', 'W', 'E', 'R', 'T', 'Y','U' ,'I', 'O', 'P'
    ]
    const Row2 = ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'];
    const Row3 = ['ENTER', 'Z', 'X', 'C', 'V', 'B', 'N', 'M', 'DEL'];

    return (
        <div className='keypad'>
            <div className='key-row'>
                {Row1.map ((elem, i) =>(
                    renderKey(elem, 'row1', i )
                ))}
            </div>
            <div className='key-row'>
                {Row2.map ((elem, i) =>(
                    renderKey(elem, 'row2', i)
                ))}
            </div>
            <div className='key-row'>
                {Row3.map ((elem, i) =>(
                    renderKey(elem, 'row3', i)
                ))}
            </div>
        </div>
    )

}
export default Keypad;
