import React, { useState } from 'react';
import Board from './Board';
import Keypad from './Keypad';
const Game  = () =>{
    const res = 'wendy';
    const [currRow, setCurrRow] = useState(0);

    const [currWord, setCurrWord] = useState('');
    const [squares, setSquares] = useState([...Array(30).fill('')]);
    const [letters, setLetters] = useState([...Array(26).fill('black')])
    const handleKeypadClick = (value) =>{
        let newWord = currWord + value;
        setCurrWord(newWord);
        let currIndex = currWord.length + Number(currRow * 5);
        let newSquares = [...squares];
        newSquares[currIndex] = value;
        setSquares(newSquares);
        setCurrWord(newWord);
        if (currWord.length == 5){
            setCurrRow(currRow+1);

        }

    }
    const updateKeypad = (currWord) =>{
        for (var i = 0; i < 5; i++){
            if (res.indexOf(currWord[i]) === i){
                const newLetters = [...letters];
                const ref = 'A';
                const index = currWord[i].charCodeAt(0)-ref.charCodeAt(0);
                console.log(index);
                newLetters[index] = '#538D4E';
                setLetters(newLetters);
            }
            else if (res.indexOf(currWord[i]) !== -1){
                const newLetters = [...letters];
                const ref = 'A';
                const index = currWord[i].charCodeAt(0)-ref.charCodeAt(0);
                console.log(index);
                newLetters[index] = '#B59F3B';
                setLetters(newLetters);
            }
            else {
                const newLetters = [...letters];
                const ref = 'A';
                const index = currWord[i].charCodeAt(0)-ref.charCodeAt(0);
                console.log(index);
                newLetters[index] = '#3A3A3C';
                setLetters(newLetters);

                console.log(letters);
            }
        }

    }
    return (
        <div className='game'>
            <Board squares = {squares} letters = {letters}></Board>
            <Keypad   handleClick = {handleKeypadClick}></Keypad>
        </div>

    )
}
export default Game;
